    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
        // TODO add your handling code here:
        
        String Name = ClientName.getText();
        String totalProdOne = ValOne.getText();
        String totalProdTwo = ValTwo.getText();
        String totalProdThree = ValThree.getText();
        
        String AmountUno = AmountOne.getText();
        String AmountDos = AmountTwo.getText();
        String AmountTres = AmountThree.getText();
        

        try{
            
        Long TvlrOne = Long.parseLong(totalProdOne) * Long.parseLong(AmountUno);
        Long TvlrTwo = Long.parseLong(totalProdTwo) * Long.parseLong(AmountDos);
        Long TvlrThree = Long.parseLong(totalProdThree) * Long.parseLong(AmountTres);
        ValTOne.setText(""+ TvlrOne);
        ValTotalTwo.setText("" + TvlrTwo );
        ValTotalThree.setText(""+ TvlrThree);
        
        Long Totaliny = TvlrOne + TvlrTwo + TvlrThree;
        
        Total.setText("" + Totaliny);
        
        if(Name != null){
        String usuario = "Se√±or/a " + Name + " el total de su compra es: " + Totaliny;
        usuari.setText(usuario); 
        }else {
        String usuario = "Por favor ingrese su nombre, el total de su compra es: " + Totaliny;
        usuari.setText(usuario); 
        }
        }catch( NumberFormatException e ){
            String error = "Digito algo erroneo o no completo los 3 productos";
            usuari.setText(error);
            return;
        };

        
        

        

    }//GEN-LAST:event_CalcularActionPerformed

